function initThemeToggle(){const e=document.getElementById("theme-toggle");e?(e.addEventListener("click",function(){console.log("Theme toggle clicked"),document.body.classList.contains("dark")?(document.body.classList.remove("dark"),localStorage.setItem("pref-theme","light"),console.log("Switched to light theme")):(document.body.classList.add("dark"),localStorage.setItem("pref-theme","dark"),console.log("Switched to dark theme"))}),console.log("Theme toggle initialized successfully")):console.error("Theme toggle button not found!")}function initTheme(){if(document.body){const e=localStorage.getItem("pref-theme");e==="dark"?document.body.classList.add("dark"):e==="light"?document.body.classList.remove("dark"):window.matchMedia("(prefers-color-scheme: dark)").matches&&document.body.classList.add("dark"),console.log("Theme initialized")}}document.readyState==="loading"?document.addEventListener("DOMContentLoaded",function(){initTheme(),initThemeToggle()}):(initTheme(),initThemeToggle())